package com.example.config;import com.mangofactory.swagger.configuration.SpringSwaggerConfig;import com.mangofactory.swagger.paths.SwaggerPathProvider;import com.mangofactory.swagger.plugin.EnableSwagger;import com.mangofactory.swagger.plugin.SwaggerSpringMvcPlugin;import com.wordnik.swagger.model.ApiInfo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.PropertySource;import org.springframework.context.support.PropertySourcesPlaceholderConfigurer;@Configuration@EnableSwagger@PropertySource({"classpath:/swagger.properties"})public class SwaggerConfig {    @Value("${swagger.basepath}")    public String basePath;    private SpringSwaggerConfig springSwaggerConfig;    @Autowired    public void setSpringSwaggerConfig(SpringSwaggerConfig springSwaggerConfig) {        this.springSwaggerConfig = springSwaggerConfig;    }    @Bean    public static PropertySourcesPlaceholderConfigurer propertiesResolver() {        return new PropertySourcesPlaceholderConfigurer();    }    @Bean    public SwaggerSpringMvcPlugin customImplementation() {        return new SwaggerSpringMvcPlugin(this.springSwaggerConfig).apiInfo(                apiInfo()).includePatterns("/.*").pathProvider(apiPathProvider());    }    @Bean    public ApiPathProvider apiPathProvider(){        ApiPathProvider apiPathProvider = new ApiPathProvider(basePath);        return apiPathProvider;    }    private ApiInfo apiInfo() {        ApiInfo apiInfo = new ApiInfo("REST API", "Retire Past Due Database REST API",                "", "tomasz.kasprzycki@bbh.com;dominik.frankowski@bbh.com",                "", "");        return apiInfo;    }}